                        {teamData.map((group, i) => (
                            <div key={i}>
                                <h3 className="section-label">{group.section}</h3>

                                <div className="circle-team-grid">
                                    {group.members.map((m, j) => (
                                        <div className="circle-card" key={j}>
                                            <div className="circle-inner">
