import React from "react";
import "./CurrentMembers.css";
import defaultPhoto from "../assets/atriya.jpg";

/* -------------------------------------------------
   SHARED LAYOUT
-------------------------------------------------- */
function MembersLayout({ title, sections, basePath }) {
  return (
    <section className="current-members-section">
      <div className="container">
        <h2 className="cm-heading">{title}</h2>

        {sections.map((group) => (
          <div className="dept-section" key={group.section}>
            <h3 className="section-label">{group.section}</h3>

            <div className="dept-members-grid">
              {group.members.map((m, i) => {
                // Resolve image source:
                // - If m.img is falsy -> use bundled defaultPhoto
                // - If m.img is a relative filename (no leading / or http) -> prepend basePath/group.folder
                // - Otherwise use m.img directly (absolute path or imported module)
                let imgSrc = defaultPhoto;
                if (m && m.img) {
                  if (typeof m.img === 'string') {
                    const isAbsolute = m.img.startsWith('/') || m.img.startsWith('http');
                    imgSrc = isAbsolute ? m.img : `${basePath}/${group.folder}/${m.img}`;
                  } else {
                    // m.img might be an imported module (resolved to a URL) - use it directly
                    imgSrc = m.img;
                  }
                }

                return (
                  <div className="member-card" key={i}>
                    <div className="member-inner">
                      <div className="member-front">
                        <img
                          src={imgSrc}
                          alt={m.name}
                          className="member-img"
                          loading="lazy"
                          onError={(e) => {
                            // prevent infinite loop by clearing handler before swapping src
                            e.currentTarget.onerror = null;
                            e.currentTarget.src = defaultPhoto;
                          }}
                        />
                      </div>

                      <div className="member-back">
                        <div className="member-back-content">
                          <p className="member-back-title">{m.name}</p>
                          <p className="member-back-details">{m.details}</p>
                        </div>
                      </div>
                    </div>

                    <h4 className="member-name">{m.name}</h4>
                  </div>
                );
              })}
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}

/* -------------------------------------------------
   FUNDAMENTAL & GENERAL PEDAGOGY
-------------------------------------------------- */
export function FundamentalPedagogyMembers() {
  const sections = [
    {
      section: "General Pedagogy",
      folder: "general-pedagogy",
      members: [
        { name: "Member 1", details: "Pedagogy Specialist", img: "1.jpg" },
        { name: "Member 2", details: "Pedagogy Specialist", img: "2.jpg" },
      ],
    },
    {
      section: "Assessment & Evaluation",
      folder: "assessment",
      members: [
        { name: "Member 3", details: "Assessment Expert", img: "1.jpg" },
      ],
    },
    {
      section: "Curriculum Design",
      folder: "curriculum",
      members: [
        { name: "Member 4", details: "Curriculum Expert", img: "1.jpg" },
      ],
    },
  ];

  return (
    <MembersLayout
      title="Fundamental & General Pedagogy "
      sections={sections}
      basePath="/members/fundamental"
    />
  );
}

/* -------------------------------------------------
   DISCIPLINE-SPECIFIC PEDAGOGY
-------------------------------------------------- */
export function DisciplinePedagogyMembers() {
  const sections = [
    {
      section: "Mathematics",
      folder: "mathematics",
      members: [
        { name: "Math Member 1", details: "Math Expert", img: "1.jpg" },
      ],
    },
    {
      section: "Science",
      folder: "science",
      members: [
        { name: "Science Member 1", details: "Science Expert", img: "1.jpg" },
      ],
    },
    {
      section: "Languages",
      folder: "languages",
      members: [
        { name: "Language Member 1", details: "Language Expert", img: "1.jpg" },
      ],
    },
  ];

  return (
    <MembersLayout
      title="Discipline-Specific Pedagogy"
      sections={sections}
      basePath="/members/discipline"
    />
  );
}

/* -------------------------------------------------
   FIELD IMPLEMENTATION TEAM (FIT)
   These sections expect images to live under: public/members/fit/<folder>/*.jpg
-------------------------------------------------- */
export function FITMembers() {
  const sections = [
    {
      section: "Members",
      folder: "fit",
      members: [
        { name: "Trainer 1", details: "Community Trainer", img: "community-trainers/1.jpg" },
        { name: "Trainer 2", details: "Community Trainer", img: "community-trainers/2.jpg" },
        { name: "Coordinator 1", details: "School Coordinator", img: "school-coordinators/1.jpg" },
        { name: "M&E 1", details: "Monitoring Specialist", img: "m_and_e/1.jpg" },
      ],
    },
  ];

  return (
    <MembersLayout
      title="Field Implementation Team (FIT)"
      sections={sections}
      basePath="/members"
    />
  );
}

export default function CurrentMembers({ sub = "fundamental", groups = null }) {
  if (sub === "discipline") {
    return <DisciplinePedagogyMembers />;
  }
  if (sub === "currentfit") {
    return <FITMembers />;
  }
  return <FundamentalPedagogyMembers />;
}
