import React, { useState, useRef } from "react";
import "./JoinForm.css";
import { sendForm } from "@emailjs/browser";

export default function JoinForm() {
  const [segment, setSegment] = useState("");
  const [arcSubSegment, setArcSubSegment] = useState("");
  const [resume, setResume] = useState(null);
  const formRef = useRef(null);

  // Configure these via environment variables or replace with your EmailJS IDs
  const EMAILJS_SERVICE_ID = process.env.REACT_APP_EMAILJS_SERVICE_ID || "service_join";
  const EMAILJS_USER_ID = process.env.REACT_APP_EMAILJS_USER_ID || "P-3I6YX10KJ6trifU";
  const EMAILJS_TEMPLATE_FIT = process.env.REACT_APP_EMAILJS_TEMPLATE_FIT || "template_fit";
  const EMAILJS_TEMPLATE_ARC = process.env.REACT_APP_EMAILJS_TEMPLATE_ARC || "template_arc";

  const [fitData, setFitData] = useState({
    fullName: "",
    displayName: "",
    servingStatus: "",
    postRetirement: "",
    workplace: "",
    qualification: "",
    designation: "",
    field: "",
    experience: "",
    publications: "",
    address: "",
    email: "",
    contact: ""
  });

  const handleFitChange = (e) => {
    setFitData({ ...fitData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formRef.current) return;

    // File size policy
    const UPLOAD_THRESHOLD = 50 * 1024; // 50 KB
    const MAX_FILE_SIZE = 2 * 1024 * 1024; // 2 MB

    // helper: upload file to file.io and return link
    const uploadToFileIo = async (file) => {
      try {
        const fd = new FormData();
        fd.append("file", file);
        const res = await fetch("https://file.io/?expires=14d", {
          method: "POST",
          body: fd,
        });
        const data = await res.json();
        // file.io returns { success: true, key, link } or { success, link }
        // Accept possible properties 'link' or 'success' + 'link' or 'url'
        if (data && (data.link || data.success && (data.link || data.key) || data.url)) {
          return data.link || data.url || (data.key ? `https://file.io/${data.key}` : null);
        }
        return null;
      } catch (err) {
        console.error("uploadToFileIo error", err);
        return null;
      }
    };

    // If ARC path and resume provided, decide attachment vs upload
    if (segment === "ARC") {
      const fileInput = formRef.current.querySelector('input[name="resume"]');
      // If user attached a file check size
      if (fileInput && fileInput.files && fileInput.files[0]) {
        const file = fileInput.files[0];
        if (file.size > MAX_FILE_SIZE) {
          alert("Resume exceeds maximum allowed size of 2 MB. Please use a smaller file.");
          return;
        }

        if (file.size > UPLOAD_THRESHOLD) {
          // Upload to file.io and include resume_link instead of attaching the file
          // Update: change your EmailJS ARC template to use {{resume_link}} (not {{resume}})
          const link = await uploadToFileIo(file);
          if (!link) {
            alert("Could not upload resume. Please try again later or use a smaller file.");
            return;
          }
          // inject hidden field resume_link
          let hidden = formRef.current.querySelector('input[name="resume_link"]');
          if (!hidden) {
            hidden = document.createElement('input');
            hidden.type = 'hidden';
            hidden.name = 'resume_link';
            formRef.current.appendChild(hidden);
          }
          hidden.value = link;
          // remove name from file input so sendForm will not include it as variable (avoid duplication)
          fileInput.removeAttribute('name');
        }
        // else: small file <=50KB will be attached by sendForm as 'resume'
      }

      try {
        const result = await sendForm(EMAILJS_SERVICE_ID, EMAILJS_TEMPLATE_ARC, formRef.current, EMAILJS_USER_ID);
        console.log("ARC email sent:", result.text);
        alert("ARC application submitted — thank you.");
        // reset small state
        setSegment("");
        setArcSubSegment("");
        setResume(null);
        // clear file input and any injected hidden resume_link
        if (formRef.current) {
          const fi = formRef.current.querySelector('input[type="file"]');
          if (fi) fi.value = null;
          const rl = formRef.current.querySelector('input[name="resume_link"]');
          if (rl) rl.remove();
        }
      } catch (err) {
        console.error("ARC email error:", err);
        alert("Failed to submit ARC application. Please try again later.");
      }
      return;
    }

    if (segment === "FIT") {
      // For FIT we do not accept file attachment here (FIT form has no resume). If you later add a file, apply same policy.
      try {
        const result = await sendForm(EMAILJS_SERVICE_ID, EMAILJS_TEMPLATE_FIT, formRef.current, EMAILJS_USER_ID);
        console.log("FIT email sent:", result.text);
        alert("FIT application submitted — thank you.");
        // reset state
        setSegment("");
        setFitData({
          fullName: "",
          displayName: "",
          servingStatus: "",
          postRetirement: "",
          workplace: "",
          qualification: "",
          designation: "",
          field: "",
          experience: "",
          publications: "",
          address: "",
          email: "",
          contact: ""
        });
        if (formRef.current) {
          const inputs = formRef.current.querySelectorAll('input');
          inputs.forEach((inp) => {
            if (inp.type !== 'radio') inp.value = '';
            else inp.checked = false;
          });
        }
      } catch (err) {
        console.error("FIT email error:", err);
        alert("Failed to submit FIT application. Please try again later.");
      }
      return;
    }
  };

  return (
    <section className={`join-form ${segment === "FIT" ? "fit-mode" : ""}`}>
      <h2>Join Us</h2>

      <form ref={formRef} onSubmit={handleSubmit}>
        {/* Hidden fields to mirror controlled state so sendForm includes them */}
        <input type="hidden" name="segment" value={segment} />
        <input type="hidden" name="subSegment" value={arcSubSegment} />
        <p>Which of the following segments interests you to contribute?</p>

        <label>
          <input
            type="radio"
            name="segment"
            onChange={() => setSegment("ARC")}
          />
          Academic Resource Council (ARC)
        </label>

        <br />

        <label>
          <input
            type="radio"
            name="segment"
            onChange={() => setSegment("FIT")}
          />
          Field Implementation Team (FIT)
        </label>
        {segment === "ARC" && (
          <>
            <p>Which sub-segment interests you to contribute?</p>

            <label>
              <input
                type="radio"
                name="arcSub"
                onChange={() =>
                  setArcSubSegment("Fundamental & General Pedagogy")
                }
              />
              Fundamental & General Pedagogy
            </label>

            <br />

            <label>
              <input
                type="radio"
                name="arcSub"
                onChange={() =>
                  setArcSubSegment("Discipline-Specific Pedagogy")
                }
              />
              Discipline-Specific Pedagogy
            </label>
            <h6>Only file with 50Kb accepted</h6>
            <p>Upload Resume</p>
            <input
              type="file"
              name="resume"
              onChange={(e) => setResume(e.target.files[0])}
            />
          </>
        )}

        {segment === "FIT" && (
          <div className="fit-grid">
            <div className="fit-field">
              <label htmlFor="fullName">Name in Full</label>
              <input id="fullName" name="fullName" placeholder="Name in Full" onChange={handleFitChange} />
            </div>

            <div className="fit-field">
              <label htmlFor="displayName">Display Name</label>
              <input id="displayName" name="displayName" placeholder="Name as you wish it to appear" onChange={handleFitChange} />
            </div>

            <div className="fit-field">
              <label htmlFor="servingStatus">Serving Status</label>
              <input id="servingStatus" name="servingStatus" placeholder="Currently Serving or Retired" onChange={handleFitChange} />
            </div>

            <div className="fit-field">
              <label htmlFor="postRetirement">Post-retirement Role</label>
              <input id="postRetirement" name="postRetirement" placeholder="Serving post-retirement" onChange={handleFitChange} />
            </div>

            <div className="fit-field">
              <label htmlFor="workplace">Current Workplace</label>
              <input id="workplace" name="workplace" placeholder="Current Workplace" onChange={handleFitChange} />
            </div>

            <div className="fit-field">
              <label htmlFor="qualification">Qualifications</label>
              <input id="qualification" name="qualification" placeholder="Qualifications" onChange={handleFitChange} />
            </div>

            <div className="fit-field">
              <label htmlFor="designation">Current Designation</label>
              <input id="designation" name="designation" placeholder="Current Designation" onChange={handleFitChange} />
            </div>

            <div className="fit-field">
              <label htmlFor="field">Field / Area of Work</label>
              <input id="field" name="field" placeholder="Current Field / Area of Work" onChange={handleFitChange} />
            </div>

            <div className="fit-field">
              <label htmlFor="experience">Years of Experience</label>
              <input id="experience" name="experience" type="number" placeholder="Years of Experience" onChange={handleFitChange} />
            </div>

            <div className="fit-field">
              <label htmlFor="publications">Research Publications</label>
              <input id="publications" name="publications" placeholder="Research Publications" onChange={handleFitChange} />
            </div>

            <div className="fit-field">
              <label htmlFor="address">Present Address</label>
              <input id="address" name="address" placeholder="Present Address" onChange={handleFitChange} />
            </div>

            <div className="fit-field">
              <label htmlFor="email">Email</label>
              <input id="email" name="email" type="email" placeholder="Email" onChange={handleFitChange} />
            </div>

            <div className="fit-field">
              <label htmlFor="contact">Contact Number</label>
              <input id="contact" name="contact" type="tel" placeholder="Contact Number" onChange={handleFitChange} />
            </div>
          </div>
        )}

        <br />
        <button type="submit">Join</button>
      </form>
    </section>
  );
}
